.context-menu-holder {
    position: fixed;
    top: 0px;
    left: 0px;
    right: 0px;
    z-index: 5;
    height: 3.5em;
    padding-top: 20px;
    overflow-x: scroll;
    overflow-y: hidden;
    padding-bottom: 20px;
}
.context-menu-holder::-webkit-scrollbar {
    display: none;
}

.context-menu {
    position: relative;
    z-index: 5;
    /* width: 350px; */
    display: block;
    text-align: left;
    margin-left: auto;
    margin-right: auto;
    border-radius: 25px;
    background-color: #60A4EB;
}
.context-menu-0 {
    width: 450px;
}
.context-menu-1 {
    width: 1120px;
}
.context-menu-2 {
    width: 1000px;
}
.context-menu-3 {
    width: 800px;
}

.context-menu-item {
    position: relative;
    width: 90px;
    height: 100%;
    color: white;
    cursor: pointer;
    font-weight: 200;
    user-select: none;
    margin-left: 10px;
    margin-right: 10px;
    text-align: center;
    font-family: 'Avenir';
    display: inline-block;
}
.context-menu-item > i {
    width: 30%;
    height: 40%;
    color: white;
    font-size: 1rem;
    padding-top: 10px;
}
.context-menu-item > p {
    margin-top: 0px;
}

.context-menu-separator {
    position: relative;
    top: 5px;
    width: 1px;
    height: 90%;
    user-select: none;
    display: inline-block;
    background-color: rgb(223, 238, 255);
}
