import { setAttributes, isHTMLElement } from '../util';

const render = function(vnode, $parent = null, instance = null, replace = false) {
    const mount = $parent ? ($el => (replace ? $parent.replaceWith($el) : $parent.appendChild($el))) : ($el => $el);

